import json

if __name__ == '__main__':
    with open('../../data/json/mission_instrument_couples_LOWER.json') as f:
        valid_couples = json.load(f)

    all_couples_list = []
    for key, list_of_values in valid_couples.items():
        for value in list_of_values:
            if value == 'not applicable':
                continue
            new_value = (key, value)
            all_couples_list.append(new_value)

    print(all_couples_list)
    print(len(all_couples_list))
